,show_tag_id=#{appIndexTagIdRelation.showTagId}
,tag_id=#{appIndexTagIdRelation.tagId}
,create_time=#{appIndexTagIdRelation.createTime}
,is_deleted=#{appIndexTagIdRelation.isDeleted}
