mapper.update:
update course_lesson_detail set
update_time = #{courseLessonDetail.updatetime}
,course_id=#{courseLessonDetail.courseId}
,lesson_name=#{courseLessonDetail.lessonName}
,lesson_link=#{courseLessonDetail.lessonLink}
,lesson_time=#{courseLessonDetail.lessonTime}
,lesson_type=#{courseLessonDetail.lessonType}
,create_time=#{courseLessonDetail.createTime}
,is_deleted=#{courseLessonDetail.isDeleted}
where id =#{courseLessonDetail.id}
