mapper.update:
update course_tag_relation set
update_time = #{courseTagRelation.updateTime}
,course_id=#{courseTagRelation.courseId}
,tag_id=#{courseTagRelation.tagId}
,create_time=#{courseTagRelation.createTime}
,is_deleted=#{courseTagRelation.isDeleted}
where id = #{courseTagRelation.id}

mapper.select:
select * from course_tag_relation


        course_id = #{courseId}


        and tag_id = #{tagId}


limit #{begin},#{size}
