)values( #{pointUserOrder.userId}, #{pointUserOrder.orderNumber}, #{pointUserOrder.supplierName}, #{pointUserOrder.merchandiseName}, #{pointUserOrder.merchandiseType}, #{pointUserOrder.merchandisePoint}, #{pointUserOrder.merchandiseNumber}, #{pointUserOrder.consumptionPointAmount}, #{pointUserOrder.orderAddress}, #{pointUserOrder.purchasedTime}, #{pointUserOrder.updateTime}, #{pointUserOrder.createTime}
